    const items = [{n:'Ø³Ø¨Ø­Ø§Ù† Ø§Ù„Ù„Ù‡', id:'t0'}, {n:'Ø§Ù„Ø­Ù…Ø¯ Ù„Ù„Ù‡', id:'t1'}, {n:'Ø§Ù„Ù„Ù‡ Ø£ÙƒØ¨Ø±', id:'t2'}];
