/* --- 5. Ø§Ù„ØªØ´ØºÙŠÙ„ Ø¹Ù†Ø¯ Ø§Ù„ØªØ­Ù…ÙŠÙ„ --- */
window.onload = () => {
    // 1. ÙØ­Øµ Ù‡Ù„ Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù… Ø§Ø®ØªØ§Ø± Ø«ÙŠÙ… Ø³Ø§Ø¨Ù‚Ø§Ù‹ØŸ
    const savedTheme = localStorage.getItem('theme');
    
    // 2. ÙØ­Øµ Ù‡Ù„ Ø¥Ø¹Ø¯Ø§Ø¯Ø§Øª Ø§Ù„Ù‡Ø§ØªÙ Ù†ÙØ³Ù‡ Dark ModeØŸ
    const prefersDark = window.matchMedia('(prefers-color-scheme: dark)').matches;

    // Ù„Ùˆ ÙÙŠÙ‡ Ø§Ø®ØªÙŠØ§Ø± Ù‚Ø¯ÙŠÙ… Ø·Ø¨Ù‚Ù‡ØŒ Ù„Ùˆ Ù…ÙÙŠØ´ Ø´ÙˆÙ Ø¥Ø¹Ø¯Ø§Ø¯Ø§Øª Ø§Ù„ÙÙˆÙ†
    if (savedTheme === 'dark' || (!savedTheme && prefersDark)) {
        document.body.classList.add('dark-mode');
    } else {
        document.body.classList.remove('dark-mode');
    }

    updateThemeUI();
    changeSection('morning');
    setTimeout(loadProgress, 200);
};


